package school.project.activate_mobileapp;

import android.os.Bundle;
import android.util.Log;
import android.view.View;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import java.util.ArrayList;

public class SearchResults extends AppCompatActivity {

    RecyclerView recyclerView;
    ArrayList<BaseClasses.Activity> source;
    RecyclerView.LayoutManager RecyclerViewLayoutManager;
    Adapter adapter;
    LinearLayoutManager HorizontalLayout;

    BaseClasses.Activity test_activity;

    View ChildView;
    int RecyclerViewItemPosition;

    @Override
    protected void onCreate(Bundle savedInstanceState)
    {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.searchresults);

        //TESTAUSTA VARTEN YKSI ACTIVITY OLIO:
        test_activity = new BaseClasses.Activity("Activity1", "Keilaus", "Keilaus el√§mys ja olutta on!\nTervetuloa tutustumaan!\n\n" ,"data:image/jpeg;base64,", 1 , 1, 19.00);
        source = BaseClasses.activities;
        Log.d("test", "SearchResults.java : source(0)");
        Log.d("test", source.get(0).toString());
        Log.d("test", source.get(0).getName());
        recyclerView
                = (RecyclerView)findViewById(
                R.id.recyclerview);
        RecyclerViewLayoutManager
                = new LinearLayoutManager(
                getApplicationContext());

        // Set LayoutManager on Recycler View
        recyclerView.setLayoutManager(
                RecyclerViewLayoutManager);

        // Adding items to RecyclerView.
        //AddItemsToRecyclerViewArrayList();

        // calling constructor of adapter
        // with source list as a parameter
        adapter = new Adapter(source);

        // Set Horizontal Layout Manager
        // for Recycler view
        HorizontalLayout
                = new LinearLayoutManager(
                SearchResults.this,
                LinearLayoutManager.HORIZONTAL,
                false);
        recyclerView.setLayoutManager(HorizontalLayout);

        // Set adapter on recycler view
        recyclerView.setAdapter(adapter);
    }

    // Function to add items in RecyclerView.
    public void AddItemsToRecyclerViewArrayList()
    {
        source = new ArrayList<BaseClasses.Activity>();
        source.add(test_activity);
    }
}


